<script type = "text/javascript" >
    var productData = <?php echo json_encode($meta_data) ?>;
    var QUICK_AR_INIT;

    function ready(callback) {
        if (document.readyState !== 'loading') callback();
        else if (document.addEventListener) document.addEventListener('DOMContentLoaded', callback);
        else document.attachEvent('onreadystatechange', function() {
                if (document.readyState === 'complete') callback();
            });
    }
    ready(createThreedyScriptTag);
    function createThreedyScriptTag() {
        const product = productData[0];
        if (product && product.quickAR && product.quickAR.edges.length > 0) {
            let quickArMetafield = JSON.parse(product.quickAR.edges[0].node.value);
            let modelMetafield = product.model.edges[0] ? JSON.parse(product.model.edges[0].node.value) : {
                hasModel: false,
                activeModel: false
            };
            let {
                initKey,
                webId,
                sku,
                has3dButton,
                hasArButton,
                hasSpaceButton,
                testing
            } = quickArMetafield;
            let {
                hasModel,
                activeModel
            } = modelMetafield;
            let enabledModel = hasModel && activeModel;
            if (!enabledModel) {
                return;
            }
            let quickArWrapper = document.querySelector("div[data-id='aritize3d-nextech']");
            quickArWrapper.classList.remove("hidden");
            QUICK_AR_INIT = {
                key: initKey
            };
            if (testing) {
                QUICK_AR_INIT['apiBase'] = '<?php echo esc_html(ARITIZE3D_APIBASE); ?>';
                QUICK_AR_INIT['qdApiBase'] = '<?php echo esc_html(ARITIZE3D_QD_APIBASE); ?>';
            }
            if (hasSpaceButton) {
                QUICK_AR_INIT['quickDesign'] = true;
            }
            quickArWrapper.setAttribute("data-threedy-web-id", webId);
            quickArWrapper.setAttribute("data-threedy-sku", "default");
            let buttonList = Array.from(quickArWrapper.children);
            let numberOfWidget = document.getElementsByClassName('view-wrapper').length;
            buttonList.forEach(btnEl => {
                if (!has3dButton && btnEl.id === "3d-button-link") {
                    btnEl.classList.add('view-button-hide');
                }
                if (!hasArButton && btnEl.id === "qar-button-link") {
                    btnEl.classList.add('view-button-hide');
                }
                if (!hasSpaceButton && btnEl.id === "qd-button-link") {
                    btnEl.classList.add('view-room-button-hide');
                }
                const activeButton = document.querySelector('.view-wrapper:not(.hidden) a[id="' + btnEl.id + '"]');
                if (numberOfWidget > 1 && activeButton !== btnEl) {
                    btnEl.addEventListener('click', () => {
                        activeButton.click();
                    })
                }
            });
            const ourScriptId = 'aritize-3d-quick-ar-script';
            const ourScript = document.getElementById(ourScriptId);
            if (!ourScript) {
                var s = document.createElement('script');
                s.id = ourScriptId;
                s.src = testing ? '<?php echo esc_html(ARITIZE3D_THREEDY_TESTING); ?>' : '<?php echo esc_html(ARITIZE3D_THREEDY_PRODUCTION); ?>';
                document.getElementsByTagName('head')[0].appendChild(s);
            }
        }
    }
</script>